<?php

use Illuminate\Database\Seeder;
use Faker\Factory as Faker;
use Carbon\Carbon;

class DatabaseSeeder extends Seeder
{

    protected $numOfUsers = 66;
    protected $numOfEvents = 66;

    protected $b64_photos = [
        'data:image/jpg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpg;base64,',
    ];


    /**
     * Seed the application's database.
     *
     * @return void
     */
    public function run()
    {
        $this->createAdmin();
        $this->generateUsers($this->numOfUsers);
        $this->generateEvents();
        $this->generateTasks();
        $this->generatePoints();
        $this->generateSocialMediaAccounts();
        $this->generateUsersEvents();
    }

    public function createAdmin()
    {
        $faker = Faker::create('ar_SA');
	        DB::table('users')->insert([
	            'first_name' => "نواف",
                'last_name' => "القعيد",
	            'is_admin' => "1",
                'student_id' => "436105865",
                'total_points'=> $faker->numberBetween(0,2000),
	            'email' => $faker->email,
	            'phone' => "0568484248",
	            'device_id' => $faker->domainName,
	            'bio' => $faker->paragraph,
                'password' => Hash::make("12345"),
                'profilephoto' => $this->b64_photos[2]
            ]);
    }

    public function generateUsers()
    {
        $faker = Faker::create('ar_SA');
        $password = '12345';
        foreach (range(2,$this->numOfUsers) as $index) {
	        DB::table('users')->insert([
                'id' => $index,
	            'first_name' => $faker->firstName,
	            'last_name' => $faker->lastName,
	            'student_id' => $faker->numberBetween(436100000,436109999),
                'email' => $faker->email,
                'total_points'=> $faker->numberBetween(0,2000),
	            'phone' => $faker->phoneNumber,
	            'device_id' => $faker->domainName,
	            'bio' => $faker->realText(100),
                'password' => Hash::make($password),
                'profilephoto' => $this->b64_photos[$faker->numberBetween(0, sizeof($this->b64_photos)-1)]
                ]);

        }
    }

    public function generateEvents()
    {
        $faker = Faker::create('ar_SA');
        foreach (range(1,$this->numOfEvents) as $index) {
	        DB::table('events')->insert([
	            'name' => $faker->catchPhrase,
	            'whatsapp_link' => $faker->domainName,
	            'leader_id' => $faker->numberBetween(1,5),
	            'user_limit' => $faker->numberBetween(3,10),
	            'date' => $faker->date,
	            'type' => $faker->randomElement(['ORGANIZE','ATTEND']),
	            'description' => $faker->realText(255),
	        ]);
        }
    }

    public function generateTasks($avrPerUser = 50)
    {
        $startDate = Carbon::now()->subWeeks(15);
        $faker = Faker::create('ar_SA');
        foreach (range(1,$avrPerUser*$this->numOfUsers) as $index) {
            $event_id = $faker->numberBetween(1, $this->numOfEvents);
            $user_id = $faker->numberBetween(1, $this->numOfUsers);
	        DB::table('tasks')->insert([
	            'description' => $faker->realText(100),
	            'user_id' => $user_id ,
	            'event_id' => $event_id ,
	            'is_approved' => $faker->numberBetween(0,1),
	            'created_at' => $faker->dateTimeBetween($startDate),
            ]);
        }
    }

    public function generatePoints($avrPerUser = 50)
    {
        $startDate = Carbon::now()->subWeeks(3);
        $faker = Faker::create('ar_SA');
        foreach (range(1,$avrPerUser * $this->numOfUsers) as $index) {
	        DB::table('points')->insert([
	            'value' => $faker->numberBetween(1,99),
	            'approved_by_admin_id' => 1 ,
	            'task_id' => $index,
                'user_id' => $faker->numberBetween(2,$this->numOfUsers),
                'updated_at' => $faker->dateTimeBetween($startDate)
            ]);

        }
    }
    public function generateSocialMediaAccounts()
    {
        $faker = Faker::create();
        $socialMediaPlatforms = ['twitter', 'steam', 'whatsapp', 'linkedin', 'snapchat'];
        foreach (range(1,$this->numOfUsers) as $index) {
        for ($i=0; ($i < sizeof($socialMediaPlatforms)) && $i<$faker->numberBetween(0, sizeof($socialMediaPlatforms)) ; $i++) {
            DB::table('socialmedia_accounts')->insert([
	            'platform' => $socialMediaPlatforms[$i],
	            'username' => $faker->userName() ,
                'user_id' => $index,
            ]);
        }
        }
    }

    public function generateUsersEvents($avrEventPerUser = 3)
    {
        $ids = array_fill(0,$this->numOfUsers , array_fill(0, $this->numOfEvents,0));
        $faker = Faker::create();
        foreach (range(1,$this->numOfUsers * $avrEventPerUser) as $index) {
            $user_id = $faker->numberBetween(1,$this->numOfUsers);
            DB::table('users_events')->insert([
                'user_id' => $user_id,
                'event_id' => $this->getEventIDAndUserID($ids ,$user_id),
            ]);
        }
    }


    private function getEventIDAndUserID(&$ids ,$user_id)
    {
        $faker = Faker::create();
        $event_id = $faker->numberBetween(1,$this->numOfEvents);
        if(isset($ids[$user_id-1]) && in_array($event_id ,$ids[$user_id-1])){

            return $this->getEventIDAndUserID($ids ,$user_id);
        }else {
            return $event_id;
        }
    }
}
